import React, { useState, useEffect, useRef } from 'react';
import { FiSend, FiWifi, FiCoffee, FiHome, FiMapPin, FiClock, FiInfo } from 'react-icons/fi';
import ReactMarkdown from 'react-markdown';

const ChatInterface = ({ socket, roomNumber, language, onLanguageChange }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const messagesEndRef = useRef(null);

  const quickActions = [
    { text: 'üçΩÔ∏è Room Service', icon: <FiCoffee /> },
    { text: 'üì∂ WiFi Info', icon: <FiWifi /> },
    { text: 'üßπ Housekeeping', icon: <FiHome /> },
    { text: 'üó∫Ô∏è Local Guide', icon: <FiMapPin /> },
    { text: '‚è∞ Late Checkout', icon: <FiClock /> },
    { text: 'üèä‚Äç‚ôÇÔ∏è Facilities', icon: <FiInfo /> }
  ];

  const languages = [
    { code: 'en', name: 'English' },
    { code: 'es', name: 'Espa√±ol' },
    { code: 'fr', name: 'Fran√ßais' },
    { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©' }
  ];

  useEffect(() => {
    if (!socket) return;

    const handleConnect = () => {
      setIsConnected(true);
      addMessage({
        type: 'assistant',
        content: getWelcomeMessage(language),
        timestamp: new Date().toISOString()
      });
    };
    const handleDisconnect = () => setIsConnected(false);
    const handleReceiveMessage = (message) => addMessage(message);
    const handleError = (error) => {
      addMessage({
        type: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date().toISOString()
      });
    };

    socket.on('connect', handleConnect);
    socket.on('disconnect', handleDisconnect);
    socket.on('receive-message', handleReceiveMessage);
    socket.on('error', handleError);

    // Cleanup listeners on unmount or dependency change
    return () => {
      socket.off('connect', handleConnect);
      socket.off('disconnect', handleDisconnect);
      socket.off('receive-message', handleReceiveMessage);
      socket.off('error', handleError);
    };
  }, [socket, language]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const addMessage = (message) => {
    setMessages(prev => [...prev, message]);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const sendMessage = () => {
    if (!inputMessage.trim() || !socket) return;

    const message = {
      type: 'user',
      content: inputMessage,
      timestamp: new Date().toISOString()
    };

    addMessage(message);
    socket.emit('send-message', {
      message: inputMessage,
      roomNumber,
      language
    });

    setInputMessage('');
  };

  const handleQuickAction = (action) => {
    setInputMessage(action.text);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const getWelcomeMessage = (lang) => {
    const messages = {
      en: "Hello! üëã Welcome to our hotel! I'm your personal virtual assistant, here to make your stay as comfortable and enjoyable as possible.\n\nI can help you with:\n‚Ä¢ üçΩÔ∏è Room service and dining\n‚Ä¢ üì∂ WiFi information\n‚Ä¢ üßπ Housekeeping requests\n‚Ä¢ ‚è∞ Late checkout arrangements\n‚Ä¢ üó∫Ô∏è Local area recommendations\n‚Ä¢ üèä‚Äç‚ôÇÔ∏è Hotel facilities information\n‚Ä¢ üí¨ General assistance\n\nHow can I assist you today?",
      es: "¬°Hola! üëã ¬°Bienvenido a nuestro hotel! Soy tu asistente virtual personal, aqu√≠ para hacer tu estad√≠a lo m√°s c√≥moda y agradable posible.\n\nPuedo ayudarte con:\n‚Ä¢ üçΩÔ∏è Servicio a la habitaci√≥n y restaurantes\n‚Ä¢ üì∂ Informaci√≥n de WiFi\n‚Ä¢ üßπ Solicitudes de limpieza\n‚Ä¢ ‚è∞ Arreglos de salida tard√≠a\n‚Ä¢ üó∫Ô∏è Recomendaciones del √°rea local\n‚Ä¢ üèä‚Äç‚ôÇÔ∏è Informaci√≥n de instalaciones del hotel\n‚Ä¢ üí¨ Asistencia general\n\n¬øC√≥mo puedo ayudarte hoy?",
      fr: "Bonjour ! üëã Bienvenue dans notre h√¥tel ! Je suis votre assistant virtuel personnel, ici pour rendre votre s√©jour aussi confortable et agr√©able que possible.\n\nJe peux vous aider avec :\n‚Ä¢ üçΩÔ∏è Service en chambre et restauration\n‚Ä¢ üì∂ Informations WiFi\n‚Ä¢ üßπ Demandes de m√©nage\n‚Ä¢ ‚è∞ Arrangements de d√©part tardif\n‚Ä¢ üó∫Ô∏è Recommandations de la r√©gion\n‚Ä¢ üèä‚Äç‚ôÇÔ∏è Informations sur les installations de l'h√¥tel\n‚Ä¢ üí¨ Assistance g√©n√©rale\n\nComment puis-je vous aider aujourd'hui ?",
      ar: "ŸÖÿ±ÿ≠ÿ®ÿßŸã! üëã ÿ£ŸáŸÑÿßŸã Ÿàÿ≥ŸáŸÑÿßŸã ŸÅŸä ŸÅŸÜÿØŸÇŸÜÿß! ÿ£ŸÜÿß ŸÖÿ≥ÿßÿπÿØŸÉ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ÿßŸÑÿ¥ÿÆÿµŸäÿå ŸáŸÜÿß ŸÑÿ¨ÿπŸÑ ÿ•ŸÇÿßŸÖÿ™ŸÉ ŸÖÿ±Ÿäÿ≠ÿ© ŸàŸÖŸÖÿ™ÿπÿ© ŸÇÿØÿ± ÿßŸÑÿ•ŸÖŸÉÿßŸÜ.\n\nŸäŸÖŸÉŸÜŸÜŸä ŸÖÿ≥ÿßÿπÿØÿ™ŸÉ ŸÅŸä:\n‚Ä¢ üçΩÔ∏è ÿÆÿØŸÖÿ© ÿßŸÑÿ∫ÿ±ŸÅ ŸàÿßŸÑŸÖÿ∑ÿßÿπŸÖ\n‚Ä¢ üì∂ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸàÿßŸä ŸÅÿßŸä\n‚Ä¢ üßπ ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿ™ŸÜÿ∏ŸäŸÅ\n‚Ä¢ ‚è∞ ÿ™ÿ±ÿ™Ÿäÿ®ÿßÿ™ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ© ÿßŸÑŸÖÿ™ÿ£ÿÆÿ±ÿ©\n‚Ä¢ üó∫Ô∏è ÿ™ŸàÿµŸäÿßÿ™ ÿßŸÑŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑŸÖÿ≠ŸÑŸäÿ©\n‚Ä¢ üèä‚Äç‚ôÇÔ∏è ŸÖÿπŸÑŸàŸÖÿßÿ™ ŸÖÿ±ÿßŸÅŸÇ ÿßŸÑŸÅŸÜÿØŸÇ\n‚Ä¢ üí¨ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ÿßŸÑÿπÿßŸÖÿ©\n\nŸÉŸäŸÅ ŸäŸÖŸÉŸÜŸÜŸä ŸÖÿ≥ÿßÿπÿØÿ™ŸÉ ÿßŸÑŸäŸàŸÖÿü"
    };
    return messages[lang] || messages.en;
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>üè® Room {roomNumber} Assistant</h2>
        <div className="language-selector">
          <span>üåê</span>
          <select 
            value={language} 
            onChange={(e) => onLanguageChange(e.target.value)}
          >
            {languages.map(lang => (
              <option key={lang.code} value={lang.code}>
                {lang.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="chat-messages">
        <div className="quick-actions">
          {quickActions.map((action, index) => (
            <button
              key={index}
              className="quick-action"
              onClick={() => handleQuickAction(action)}
            >
              {action.icon} {action.text}
            </button>
          ))}
        </div>

        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="message-avatar">
              {message.type === 'user' ? 'üë§' : 'ü§ñ'}
            </div>
            <div className="message-content">
              <ReactMarkdown>{message.content}</ReactMarkdown>
              <div className="message-time">
                {formatTime(message.timestamp)}
              </div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input">
        <textarea
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder={language === 'ar' ? "ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ŸáŸÜÿß..." : "Type your message here..."}
          rows="1"
        />
        <button
          className="send-button"
          onClick={sendMessage}
          disabled={!inputMessage.trim() || !isConnected}
        >
          <FiSend />
        </button>
      </div>

      <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
        {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
      </div>
    </div>
  );
};

export default ChatInterface;
